def part_one(instructions):
    f = 0
    for i in instructions:
        if i == "(":
            f += 1
        else:
            f -= 1

    return f

def part_two(instructions):
    f = 0
    for i in range(len(instructions)):
        if instructions[i] == "(":
            f += 1
        else:
            f -= 1
        if f == -1:
            return i + 1 
    return 0

print(part_one(instructions))
print(part_two(instructions))